CREATE TABLE CATEGORIE (NOM VARCHAR(20) NOT NULL, IMAGE VARCHAR(200), DESCRIPTION VARCHAR(200), PRIMARY KEY (NOM));

CREATE TABLE RACE (NOM VARCHAR(30) NOT NULL, CATEGORIE_NOM VARCHAR(30) NOT NULL, IMAGE VARCHAR(200), DESCRIPTION VARCHAR(200), PRIMARY KEY (NOM));

CREATE TABLE PERSONNE (IDENTIFIANT DECIMAL(5) NOT NULL, LOGIN VARCHAR(20) NOT NULL,PWD VARCHAR(10) NOT NULL, NOM VARCHAR(30) NOT NULL, PRENOM VARCHAR(30) NOT NULL, DESIGNATION CHAR(4) NOT NULL, SEXE CHAR(1), DATEDENAISSANCE DATE, ADRESSE VARCHAR(200), CODEPOSTAL VARCHAR(8), VILLE VARCHAR(20), PAYS VARCHAR(20), TELEPHONE VARCHAR(20), TELEPHONEPORTABLE VARCHAR(20), EMAIL VARCHAR(100), NOMBREDECONNEXION DECIMAL(15) NOT NULL, DERNIERECONNEXION DATE NOT NULL, PRIMARY KEY (IDENTIFIANT));


CREATE TABLE ANIMAL (IDENTIFIANT DECIMAL(15) NOT NULL, NUMEROREGISTRE VARCHAR(30), NOM VARCHAR(30) NOT NULL, SEXE CHAR(1) NOT NULL, DATEDENAISSANCE DATE, DATEDECES DATE, DESCRIPTION VARCHAR(200), PROPRIETAIRE VARCHAR(20), NOM_RACE VARCHAR(30), IMAGE VARCHAR(200), TARIF DOUBLE, PRIMARY KEY (IDENTIFIANT));

